import React from "react";
import '../CSS/ContactForm.css';
import {Navbar,Nav} from "react-bootstrap";
import {Link} from "react-router-dom";
import Footer from "../StorySection/Footer";
import BackgroundParticle from "../background";
import emailjs from 'emailjs-com';
function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('service_aexnouj', 'template_82xujve', e.target, 'user_o2uWOUUEZIpA3AxFDi7dE')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  }
function ContactForm() {
    return (
        <>
		<BackgroundParticle/>
		<div style={{position: "absolute",top: 0,left: 0,width: "100vw",height: "100vh"}} >
			<Navbar bg="light" expand="lg" variant="light">
				<Link to="/"><Navbar.Brand style={{fontFamily:"Raleway",fontWeight:"bold"}}>THE DATA ANECDOTES</Navbar.Brand></Link>
				<Navbar.Toggle aria-controls="responsive-navbar-nav" />
				<Navbar.Collapse id="responsive-navbar-nav">
				<Nav className="ml-auto">
				<Nav.Link href="/"><p style={{color:"black"}}>Home</p></Nav.Link>
				<Nav.Link href="/Stories"><p style={{color:"black"}}>Stories</p></Nav.Link>
				<Nav.Link href="/OurTeam" style={{color:"black"}}>Our Team </Nav.Link>
				<Nav.Link href="/ContactUs" style={{color:"black"}}>Contact Us</Nav.Link>
				{/*<Nav.Link href="/ContactUs" style={{color:'white'}}>Floral Boxes</Nav.Link>*/}

				</Nav>
				</Navbar.Collapse>

			</Navbar>

			<div className="wrapper">
				<div className="inner">
					<form action="" onSubmit={sendEmail}>
						<h3 className="customh3">Contact Us</h3>
						<p className="customp">Liked our work and want to collaborate? Get your hands on any dataset we worked on or drop in your feedback maybe ? We would like to extend our warm reach to you. 
						Leave us a message and we promise to get back to you soon.</p>
						<label className="form-group">
						<input type="text" name="from_name" className="form-control"  required/>
							<span>Your Name</span>
							<span className="border"></span>
						</label>
						<label className="form-group">
						<input type="email" name="user_email" className="form-control"  required/>
							<span for="">Your Mail</span>
							<span className="border"></span>
						</label>
						<label className="form-group" >
							<textarea name="message" id="" className="form-control" required></textarea>
							<span for="">Your Message</span>
							<span className="border"></span>
						</label>
						<input type="submit" value="Send" className="button2"/>
					</form>
				</div>
			</div>
			<Footer/>
		</div>
        </>
    );
}
export default ContactForm;
